import React from "react";
import { useState } from "react";
import HeroImage from "../imgs/hero.png";
import { Button, Card, Carousel, Container, Image } from "react-bootstrap";
import { PopupModal } from "react-calendly";

export default class Hero extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isOpen: false,
    };
  }

  render() {
    return (
      <>
        <Container>
          <Image src={HeroImage} fluid className="w-50 h-auto" />
          {/*        <Card className="bg-dark text-white">
          <Card.Img src={HeroImage} alt="Card image" /> */}
          {/*  <Card.ImgOverlay> */}
          {/*    <Card.Title>CREATE YOUR BALANCE</Card.Title> */}
          <h1>CREATE YOUR BALANCE</h1>
          <Button
            variant="primary"
            onClick={() => this.setState({ isOpen: true })}
          >
            BOOK A SESSION
          </Button>{" "}
          <PopupModal
            url="https://calendly.com/sadefryt"
            pageSettings={this.props.pageSettings}
            utm={this.props.utm}
            onModalClose={() => this.setState({ isOpen: false })}
            open={this.state.isOpen}
            rootElement={document.getElementById("root")}
          />
        </Container>
        {/*   </Card.ImgOverlay> */}
        {/*  </Card> */}
      </>
    );
  }
}
